# Convert hex string to byte array function
function FEKBr($BnZNoTk) {
    return -split ($BnZNoTk -replace '..', '0x$& ')
}

# Original hex string to be decoded
$QUBeoH = FEKBr('')

# Create the AES decryptor
$uSjoD = [System.Security.Cryptography.Aes]::Create()
$uSjoD.Key = FEKBr('4F4E6E734E4F6B626E61656561576378')
$uSjoD.IV = New-Object byte[] 16

# Create the decryptor and decrypt the byte array
$MBqYOZdV = $uSjoD.CreateDecryptor()
$OHWozKdrW = $MBqYOZdV.TransformFinalBlock($QUBeoH, 0, $QUBeoH.Length)

# Convert decrypted byte array to UTF-8 string
$KrNFtgiCg = [System.Text.Encoding]::Utf8.GetString($OHWozKdrW)

# Dispose of the decryptor
$MBqYOZdV.Dispose()

# Print the decrypted content to the screen
Write-Host "Decoded content: $KrNFtgiCg"